import React, {useState} from 'react'
import {BsThreeDots} from "react-icons/bs";
import { AiFillHeart, AiOutlineHeart} from "react-icons/ai";
import {FaRegComment} from "react-icons/fa";
import {RiSendPlaneLine} from "react-icons/ri";

const Post = () => {
    const [isPostLiked, setIsPostLiked]=useState(false);

    const handlePostLike=()=>{
        setIsPostLiked(!isPostLiked);
    }
    return(
        <div>
            <div className="border rounded-md w-full">
                <div className="flex justify-between items-center w-full px-4 py-5">
                    <div className="flex items-center">
                        <img className="h-12 w-12 rounded-full" src="https://cdn.pixabay.com/photo/2022/02/06/14/06/dog-6997211_640.jpg" alt=""/>
                        <div className="pl-2">
                            <p className="font-semibold text-sm">username</p>
                            <p className="font-thin text-sm">location</p>
                        </div>
                    </div>
                    <div>
                        <BsThreeDots/>
                    </div>
                </div>

                <div className="w-full">
                    <img className="w-full" src="https://cdn.pixabay.com/photo/2016/07/15/15/55/dachshund-1519374_640.jpg" alt=""/>
                </div>

                <div className="flex justify-between items-center w-full px-5 py-4">
                    <div className="flex items-center space-x-2">
                        {isPostLiked? <AiFillHeart className="text-2xl hover:opacity-50 cursor-pointer text-red-600" onClick={handlePostLike}/>:
                        <AiOutlineHeart className="text-2xl hover:opacity-50 cursor-pointer" onClick={handlePostLike}/>}

                        <FaRegComment className="text-xl hover:opacity-50 cursor-pointer"/>
                        <RiSendPlaneLine className="text-xl hover:opacity-50 cursor-pointer"/>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Post
